  };

  const match = (array) => {
    const endHeaders = backEndHeaders.map((header) => {
      return header.name;
    });
    const matched = array.map((item, index) => {
      const values = item.values;
      const valueErrors = values.filter((value, index) => {
        return value.length === 0;
      });
      let obj = {
        name: item.name,
        values: item.values,
        headerMatch: {
          match: endHeaders.includes(item.name),
          name: endHeaders.filter((header) => header.includes(item.name)),
        },
        headerValues: {
          match: valueErrors.length === 0,
          errors: valueErrors,
        },
      };
      return obj;
    });
    setHeaders(matched);
  };

  useEffect(() => {
    if (data.length > 0) {
      let headers = Object.keys(data[0]);
      let newArr = headers.map((header, index) => {
        let values = data.map((item, index) => {
          return item[`${header}`];
        });
        let obj = {
          name: header,
          values: values,
        };
        return obj;
      });
      match(newArr);
    }
  }, [data, backEndHeaders]);


  if (file) {
    return (
      <div className="match">
        <div className="match-header">{file.name}</div>
        <div className="main">
          {headers.map((header, index) => {
            return (
              <CsvHeader
                key={index}
                data={data}
                header={header}
                endHeaders={backEndHeaders}
                updateMatchedHeader={updateMatchedHeader}
                setSelectedHeader={setSelectedHeader}
                selectedHeader={selectedHeader}
              />
            );
          })}
        </div>
      </div>
    );
  } else {
    return <Redirect to="/" />;
  }
};

export default Match;
