import React, { useState, useEffect } from "react";
import { Redirect, Link } from "react-router-dom";
import { FixedSizeList as List } from "react-window";
import AutoSizer from "react-virtualized-auto-sizer";
import "../css/Review.css";
const Review = ({ file, data, setFrame }) => {
  const [reviewData, setReviewData] = useState([]);
  const [headers, setHeaders] = useState([]);
  const [rows, setRows] = useState([]);

  useEffect(() => {
    if (data.length > 0) {
      const headers = Object.keys(data[0]);
      const rows = data.map((row) => {
        const values = headers.map((header) => {
          return row[header];
        });
        return values;
      });
      setHeaders(headers);
      setRows(rows);
    }
  }, [data]);

  useEffect(() => {
    if (data.length > 0) {
      let keys = Object.keys(data[0]);
      let newArr = keys.map((header, index) => {
        let values = data.map((item, index) => {
          return item[header];
        });
        let obj = {
          name: header,
          values: values,
        };
        return obj;
      });
      setReviewData(newArr);
    }
  }, []);

  const Row = ({ index, style }) => (
    <div style={style} key={index} className="review-table-cell">
      <p>{rows[index]}</p>
    </div>
  );

  // const renderRows = () => {
  //   return (

  //   );
  // };

  if (data.length > 0) {
    return (
      <div className="review">
        <div className="review-header">
          <h3>{file.name}</h3>
          <Link
            className="trove-button"
            style={{ textDecoration: "none" }}
            onClick={() => setFrame("Complete")}
            to="/complete"
          >
            Complete
          </Link>
        </div>
        <div className="review-table">
          <div className="review-table-headers">
            {headers.map((header, index) => {
              return (
                <div key={index} className="review-table-header">
                  <p>{header}</p>{" "}
                </div>
              );
            })}
          </div>
          <AutoSizer>
            {({ height, width }) => {
              <List
                height={height}
                width={width}
                itemCount={rows.length}
                itemSize={30}
              >
                {Row}
              </List>;
            }}
          </AutoSizer>
          {/* {reviewData.map((item, index) => {
            return (
              <div key={index} className="review-table-col">
                <div className="review-table-headers">
                  <div className="review-table-header">
                    <p>{item.name}</p>
                  </div>
                </div>
                {item.values.map((value, index) => {
                  return (
                    <div key={index} className="review-table-cell">
                      <p>{value}</p>
                    </div>
                  );
                })}
              </div>
            );
          })} */}
        </div>
      </div>
    );
  } else {
    return <Redirect to="/" />;
  }
};

export default Review;
